import{_ as i,o as r,c as t,p as o,a as d,b as e}from"./index-6795101c.js";const a={},l=n=>(o("data-v-0b38f441"),n=n(),d(),n),s={class:"kleurmodellen-page"},u=l(()=>e("h1",null,"kleur-modellen",-1)),c=l(()=>e("p",null,"de kleurmodellen RGB en CMY-K behandelt, inclusief een uitleg over pixels en resolutie",-1)),h=l(()=>e("div",{class:"uitleg-kleur"},[e("h3",null,"RGB en CMYK zijn twee verschillende kleurmodellen die worden gebruikt in grafisch ontwerp, fotografie en afdrukken. Ze zijn bedoeld voor verschillende toepassingen en werken op verschillende principes. Hier is een uitleg van beide kleurmodellen, samen met informatie over pixels en resolutie:"),e("div",{class:"uitlegRGB"},[e("h2",null,"RGB (Rood-Groen-Blauw):"),e("p",null,"RGB is een additief kleurmodel dat wordt gebruikt in digitale schermen, zoals computermonitors, televisies en camera's. In het RGB-model worden kleuren gecreëerd door verschillende hoeveelheden rood, groen en blauw licht te combineren. De basisgedachte hierachter is dat wanneer je volledige intensiteit van elk van deze drie kleuren toevoegt, je wit licht krijgt. De afwezigheid van alle kleuren resulteert in zwart."),e("li",null,"Rood (Red), Groen (Green), Blauw (Blue): Dit zijn de drie primaire kleuren in het RGB-model. Door verschillende intensiteiten van deze kleuren te mengen, kun je een breed scala van kleuren produceren."),e("li",null,'Pixels: Een pixel (afkorting voor "picture element") is de kleinste eenheid van een digitaal beeld. Elk pixel kan een kleur vertegenwoordigen volgens het RGB-model. Een afbeelding op een scherm is opgebouwd uit miljoenen pixels die samen een beeld vormen.'),e("li",null,"Resolutie: De resolutie van een scherm of afbeelding wordt gemeten in pixels per inch (PPI) of dots per inch (DPI). Het geeft aan hoeveel pixels er in een bepaald gebied zijn. Hoe hoger de resolutie, hoe scherper en gedetailleerder de afbeelding kan zijn.")]),e("div",{class:"uitlegCMYK"},[e("h2",null,"CMYK (Cyaan, Magenta, Geel, Zwart):"),e("p",null,"CMYK is een subtractief kleurmodel dat voornamelijk wordt gebruikt in de drukindustrie. In tegenstelling tot het additieve RGB-model, werkt het CMYK-model door kleuren af te trekken van een witte achtergrond. De vier kleuren, cyaan, magenta, geel en zwart (key), worden in verschillende verhoudingen gecombineerd om verschillende kleuren te produceren."),e("li",null,'Cyaan, Magenta, Geel, Zwart: Deze kleuren worden gebruikt in de drukindustrie om gedrukte afbeeldingen te produceren. Zwart wordt "key" genoemd omdat het verantwoordelijk is voor het definiëren van de belangrijkste details en contouren in afdrukken.'),e("li",null,"Pixels: Net als in het RGB-model zijn pixels ook aanwezig in digitale afbeeldingen die bedoeld zijn voor afdrukken in CMYK-kleuren. Elke pixel bevat informatie over de hoeveelheid cyaan, magenta, geel en zwart die nodig is om de gewenste kleur te produceren."),e("li",null,"Resolutie: In de afdrukwereld wordt resolutie gemeten in dots per inch (DPI). Een hogere DPI-waarde resulteert in een scherpere afdruk. Bij het omzetten van een RGB-afbeelding naar CMYK, moet je rekening houden met kleurconversie en verlies van helderheid, omdat het kleurbereik van CMYK kleiner is dan dat van RGB.")]),e("iframe",{width:"560",height:"315",src:"https://www.youtube.com/embed/9hirYMZ7PQc?si=dBzNuCx1d4LOPWy6",title:"YouTube video player",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share",allowfullscreen:""})],-1)),k=[u,c,h];function g(n,m){return r(),t("main",s,k)}const v=i(a,[["render",g],["__scopeId","data-v-0b38f441"]]);export{v as default};
